package chapter01;

public class OperatorTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=7;
		int b=3;
		
		System.out.println(a>b);
		System.out.println(a<b);
		
		boolean result=(a>b);
		System.out.println(result);
		
		result=(a==b);
		System.out.println(result);

	}

}
